import { createSelector } from "reselect";

import { COLLECTION_ITEM_ROUTE_ID_MAP } from "./shopConstants";

const selectShop = (state) => state.shop;

export const selectShopItems = createSelector(
  [selectShop],
  (shop) => shop.shopItems
);

export const selectShopCollection = (itemId) =>
  createSelector([selectShopItems], (shopItems) =>
    shopItems.find((item) => item.id === COLLECTION_ITEM_ROUTE_ID_MAP[itemId])
  );
