export const CartActionTypes = {
  setCartIsHidden: "SET_CART_IS_HIDDEN",
};

export const setCartIsHidden = (cartIsHidden) => {
  return {
    type: CartActionTypes.setCartIsHidden,
    payload: cartIsHidden,
  };
};
